query GetProduct($req: ProductRequest!) {
  product: getProduct(req: $req) {
    ...productFields
    bundled { ...productFields }
    crossSell { ...productFields }
  }
}

fragment productFields on ProductResponse {
  id
  shortName
  longName
  productCategory
  productType
  productCode
  subProductCode
  campaigns {
    sourceCode
    totalRate
    bonusRate
    baseRate
    rateSuffix
    duration
  }
}
