query:
mutation SubmitApplication(
  $product: ProductSelection,
  $appData: AppDataInput,
  $ids: IDs!,
  $isSubmit: Boolean!
) {
  submitApplication(
    product: $product,
    appData: $appData,
    ids: $ids,
    isSubmit: $isSubmit,
  ) {
    basket
    orders{
      orderID{
        orderNumber
        version
      }
      message
    }
  }
}
